import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import UseApi from "../../Shared/Hooks/UseApi";
import ButtonComponente from "../../Components/Container/ButtonComponente";
import * as S from '../../Styles/Global/SignupSigninStyles'

const SignupScreens = () => {
  const [name, setName] = useState<string>("");
  const [email, setEmail] = useState<string>("");
  const [emailConf, setEmailConf] = useState<string>("");
  const [senha, setSenha] = useState<string>("");
  const [error, setError] = useState<string>("");
  const navigate = useNavigate();

  const { signup } = UseApi();

  const handleSignup = async () => {
    if (!email || !emailConf || !senha || !name) {
      setError("Preencha todos os campos");
      return;
    } else if (email !== emailConf) {
      setError("Os e-mails não são iguais");
      return;
    }
    try {
      const res = signup(email, senha, name);

      if (res) {
        setError(res);
      } else {
        alert("Usuário cadastrado com sucesso!");
        navigate("/");
      }
    } catch (error) {
      setError("Erro ao cadastrar usuário");
    }
  };

  return (
    <S.Container>
      <h2> Pagina de Cadastro</h2>
        <S.Content>
          <input
            type="text"
            placeholder="Digite seu Name"
            value={name}
            onChange={(e) => [setName(e.target.value), setError("")]}
          />
          <input
            type="email"
            placeholder="Digite seu E-mail"
            value={email}
            onChange={(e) => [setEmail(e.target.value), setError("")]}
          />
          <input
            type="email"
            placeholder="Confirme seu E-mail"
            value={emailConf}
            onChange={(e) => [setEmailConf(e.target.value), setError("")]}
          />
          <input
            type="password"
            placeholder="Digite sua Senha"
            value={senha}
            onChange={(e) => [setSenha(e.target.value), setError("")]}
          />
        <p dangerouslySetInnerHTML={{ __html: error }}></p>
        <ButtonComponente onClick={handleSignup} titulo={"Inscrever-se"}></ButtonComponente>
        <p>Já tem uma conta?</p>
        <div>
          <Link to="/">&nbsp;Entre</Link>
        </div>
      </S.Content>
    </S.Container>
  );
};

export default SignupScreens;
