import { useEffect, useState, ReactNode, createContext, useContext } from "react";
import { User } from "../../Types/User";

// ... (importações e interfaces)

interface AuthProviderProps {
  children: ReactNode;
}

interface AuthContextValue {
  user: User | null;
  signed: boolean;
  signin: (email: string, password: string) => Promise<void>;
  signup: (email: string, password: string) => Promise<void>;
  signout: () => void;
}

export const AuthContext = createContext<AuthContextValue | undefined>(undefined);

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userToken = localStorage.getItem("user_token");
        const usersStorage = localStorage.getItem("users_bd");

        if (userToken && usersStorage) {
          const hasUser = JSON.parse(usersStorage).find(
            (storedUser: User) => storedUser.email === JSON.parse(userToken).email
          );

          if (hasUser) {
            setUser(hasUser);
          }
        }
      } catch (error: any) {
        console.error('Erro ao carregar dados do usuário:', error);
      }
    };

    fetchData();
  }, []);

  const signin = async (email: string, password: string): Promise<void> => {
    try {
      setLoading(true);

      const usersStorage = JSON.parse(localStorage.getItem("users_bd") || "[]");

      const hasUser = usersStorage.find((storedUser: User) => storedUser.email === email);

      if (hasUser) {
        if (hasUser.email === email && hasUser.password === password) {
          const token = Math.random().toString(36).substring(2);
          localStorage.setItem("user_token", JSON.stringify({ email, token }));
          setUser({ ...hasUser, password });
        } else {
          throw new Error("E-mail ou senha incorretos");
        }
      } else {
        throw new Error("Usuário não cadastrado");
      }
    } catch (error: any) {
      console.error('Erro ao fazer login:', error);
      throw error;
    } finally {
      setLoading(false);
    }
  };

  const signup = async (email: string, password: string): Promise<void> => {
    try {
      setLoading(true);

      const usersStorage = JSON.parse(localStorage.getItem("users_bd") || "[]");

      const hasUser = usersStorage.find((storedUser: User) => storedUser.email === email);

      if (hasUser) {
        throw new Error("Já existe uma conta com este e-mail");
      }

      const newUser: User[] = [...usersStorage, { email, password }];
      localStorage.setItem("users_bd", JSON.stringify(newUser));
    } catch (error: any) {
      console.error('Erro ao fazer cadastro:', error);
      throw error;
    } finally {
      setLoading(false);
    }
  };

  const signout = (): void => {
    setUser(null);
    localStorage.removeItem("user_token");
  };

  const authContextValue: AuthContextValue = {
    user,
    signed: !!user,
    signin,
    signup,
    signout,
  };

  return (
    <AuthContext.Provider value={authContextValue}>
      {children}
    </AuthContext.Provider>
  );
};
