import { ChangeEvent, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import UseApi from "../../Shared/Hooks/UseApi";
import ButtonComponente from "../../Components/Container/ButtonComponente";

const SigninScreens: React.FC = () => {
  const { signin } = UseApi();
  const navigate = useNavigate();

  const [email, setEmail] = useState<string>("");
  const [senha, setSenha] = useState<string>("");
  const [error, setError] = useState<string>("");

  const handleLogin = async () => {
    if (!email || !senha) {
      setError("Preencha todos os campos");
      return;
    }

    try {
      const res = await signin(email, senha);

      if (res) {
        setError(res);
        return;
      }

      navigate("/home");
    } catch (error) {
      console.error("Erro ao efetuar login:", error);
    }
  };

  const handleEmailChange = (e: ChangeEvent<HTMLInputElement>) => {
    setEmail(e.target.value);
    setError("");
  };

  const handlePasswordChange = (e: ChangeEvent<HTMLInputElement>) => {
    setSenha(e.target.value);
    setError("");
  };

  return (
    <div>
      <h2> Pagina de Login</h2>

      <input
        type="text"
        value={email}
        onChange={handleEmailChange}
        placeholder="Digite seu e-mail"
      />
      <input
        type="password"
        value={senha}
        onChange={handlePasswordChange}
        placeholder="Digite sua senha"
      />
      <p dangerouslySetInnerHTML={{ __html: error }}></p>
      <ButtonComponente onClick={handleLogin}>Logar</ButtonComponente>

      <p> NÃ£o tem uma conta?</p>
      <div>
        <Link to="/signup">&nbsp;Registre-se</Link>
      </div>
    </div>
  );
};

export default SigninScreens;
